mapper.select:
/* com.example.W2_board0_jar.dao.board.BoardDao */ SELECT BOARD_TYPE AS boardType , CASE BOARD_TYPE WHEN 'a01' THEN '일반' WHEN 'a02' THEN 'QA' WHEN 'a03' THEN '익명' WHEN 'a04' THEN '자유' ELSE '기타' END AS boardTypeName , BOARD_NUM AS boardNum , BOARD_TITLE AS boardTitle , BOARD_COMMENT AS boardComment FROM BOARD
WHERE BOARD_TYPE = #{codeId}
ORDER BY BOARD_NUM DESC

/* com.example.W2_board0_jar.dao.board.BoardDao */
SELECT      COUNT(*)
FROM        BOARD

WHERE           BOARD_TYPE = #{codeId}

mapper.insert:
/* com.example.W2_board0_jar.dao.board.BoardDao */

    SELECT NVL(MAX(BOARD_NUM), 0) + 1 AS boardNum FROM BOARD

INSERT INTO 	BOARD
(
                BOARD_NUM
        ,       BOARD_TYPE
        ,		BOARD_TITLE
        ,		BOARD_COMMENT
        ,		CREATOR
        ,       CREATE_TIME
) VALUES (
                #{boardNum}
         ,      #{boardType}
         ,		#{boardTitle}
         ,		#{boardComment}
         ,		#{userId}
         ,		SYSDATE
         )
