mapper.insert:

    SELECT IFNULL(MAX(boardNum), 0) + 1 FROM BOARD

INSERT INTO 	BOARD
(
        BOARD_NUM
,       BOARD_TYPE
,		BOARD_TITLE
,		BOARD_COMMENT
,		CREATOR
,		CREATE_TIME
) VALUES (
        #{boardNum}
,       #{boardType}
,		#{boardTitle}
,		#{boardComment}
,		#{creater}
,		NOW()
)

mapper.update:
UPDATE 		tb_board
SET 		board_name	    = 	#{board_name}
  ,	        board_content 	= 	#{board_content}
  ,	        user_name 		= 	#{user_name}
  ,  	    board_date 	    =   NOW()
WHERE 		board_no        =   #{board_no}
